INSERT INTO `wp_options` VALUES("1389", "_transient_dash_4077549d03da2e451c8b5f002294ff51", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2014/12/dinah/\'>WordPress 4.1 Dinah</a> <span class=\"rss-date\">December 18, 2014</span><div class=\"rssSummary\">Version 4.1 of WordPress, named Dinah in honor of jazz singer Dinah Washington, is available for download or update in your WordPress dashboard. New features in WordPress 4.1 help you focus on your writing, and the new default theme lets you show it off in style. Introducing TwentyFifteen Our newest default theme, Twenty Fifteen, is [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/first-look-at-sidekick\'>WPTavern: First Look at SIDEKICK</a></li><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/01/fasting/\'>Matt: Fasting</a></li><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/01/the-right-problem/\'>Matt: The Right Problem</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/jetpack/\' class=\'dashboard-news-plugin-link\'>Jetpack by WordPress.com</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=jetpack&amp;_wpnonce=65f4c43f56&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Jetpack by WordPress.com\'>Install</a>)</span></li></ul></div>", "no");
