INSERT INTO `wp_postmeta` VALUES("462", "43", "project_design_challenge", "How might we design a retail experience that inspires customers and staff through the power of sound and vision?");
INSERT INTO `wp_postmeta` VALUES("463", "43", "_project_design_challenge", "field_54adce7f87b2d");
INSERT INTO `wp_postmeta` VALUES("464", "43", "project_approach", "<ul>\r\n	<li>\r\n<div>Immersed 13 staff from director level to mangers and sales staff in 5 hands-on experiential workshops over 3 weeks using innovative research, problem solving and ideation tools</div></li>\r\n	<li>\r\n<div>Guided participants to take a customer-centric view while practicing research techniques</div></li>\r\n	<li>\r\n<div>Analyzed inspiring industry and non-industry related case studies to fuel the ideation process to create out-of-the box retail ideas</div></li>\r\n	<li>\r\n<div>Led participants to prioritize the ideas and build prototypes that were tested with their customers and staff</div></li>\r\n	<li>\r\n<div>Taught participants our problem-solving tools for everyday creative solutioning</div></li>\r\n</ul>");
INSERT INTO `wp_postmeta` VALUES("465", "43", "_project_approach", "field_54adced487b2f");
INSERT INTO `wp_postmeta` VALUES("466", "43", "project_impact", "<ul>\r\n	<li>\r\n<div>Management rated us 4.6/5 for overall client impact</div></li>\r\n	<li>\r\n<div>100% of Atlas participants say they would recommend us for another project; in fact, we were invited to conduct a re-run of the program</div></li>\r\n	<li>\r\n<div>Management saw visible increase in staff engagement</div></li>\r\n	<li>\r\n<div>Staff felt empowered to deliver the redesigned retail experience</div></li>\r\n</ul>");
INSERT INTO `wp_postmeta` VALUES("467", "43", "_project_impact", "field_54adcefb87b30");
INSERT INTO `wp_postmeta` VALUES("468", "43", "project_testimonials", "I hope that the team and colleagues who are stakeholders see the change (transformation) in themselves.  Sherwin Siregar, Deputy CEO");
INSERT INTO `wp_postmeta` VALUES("469", "43", "_project_testimonials", "field_54adcea987b2e");
