INSERT INTO `wp_postmeta` VALUES("521", "108", "mission_consciousness", "We use assessment tools and diagnostic methods to raise the level of consciousness both individually and within organizations to identify what works, what doesnt work, and where opportunities are for growth and development in order to achieve specific strategic objectives.");
