INSERT INTO `wp_posts` VALUES("47", "1", "2015-01-07 04:59:30", "2015-01-07 04:59:30", "At Awaken Group, we pride ourselves in being socially-conscious. Thus, we find great importance in giving back our expertise to the community. We are a social enterprise that donates from our top-line revenue and invests it back into the community to develop leaders around the world. We live out our values through financial support to non-profit organizations and provide in-kind services, such as speaking, conducting workshops, and consulting.", "Give", "", "inherit", "open", "open", "", "45-revision-v1", "", "", "2015-01-07 04:59:30", "2015-01-07 04:59:30", "", "45", "http://agwp.dev/2015/01/45-revision-v1/", "0", "revision", "", "0");
