INSERT INTO `wp_posts` VALUES("80", "1", "2015-01-07 01:32:53", "2015-01-07 09:32:53", "Awaken Group is a multi-disciplinary Transformation Design (TD) firm that integrates strategy, leadership, and innovation to design human-centered experiences. We sit at the intersection of business, people, and design to provide purpose-driven sustainable solutions. Simply put, we Consult + Coach + Design + Digitize.\r\n\r\nWe work in the intangible (vision, strategy, leadership, organization, CEO coaching, process) and tangible (experience design, space, product/service innovation, branding) to design transformative experiences from the inside out to bring joy and beauty to the world.", "About", "", "publish", "open", "open", "", "about", "", "", "2015-01-14 16:27:38", "2015-01-15 00:27:38", "", "0", "http://agwp.dev/?page_id=80", "0", "page", "", "0");
