INSERT INTO `wp_posts` VALUES("106", "1", "2015-01-09 18:52:48", "2015-01-10 02:52:48", "Awaken Group is a multi-disciplinary Transformation Design (TD) firm that integrates strategy, leadership, and innovation to design human-centered experiences. We sit at the intersection of business, people, and design to provide purpose-driven sustainable solutions. Simply put, we CONSULT + COACH + DESIGN + DIGITIZE.\r\n\r\nWe work in the intangible (vision, strategy, leadership, organization, CEO coaching, process) and tangible (experience design, space, product/service innovation, branding) to design transformative experiences from the inside out to bring joy and beauty to the world.", "About", "", "inherit", "open", "open", "", "80-revision-v1", "", "", "2015-01-09 18:52:48", "2015-01-10 02:52:48", "", "80", "http://agwp.dev/2015/01/80-revision-v1/", "0", "revision", "", "0");
