INSERT INTO `wp_posts` VALUES("142", "1", "2015-01-14 16:23:56", "2015-01-15 00:23:56", "Awaken Group is a multi-disciplinary Transformation Design (TD) firm that integrates strategy, leadership, and innovation to design human-centered experiences. We sit at the intersection of business, people, and design to provide purpose-driven sustainable solutions. Simply put, we Consult + Coach + Design + Digitize.\r\n\r\nWe work in the intangible (vision, strategy, leadership, organization, CEO coaching, process) and tangible (experience design, space, product/service innovation, branding) to design transformative experiences from the inside out to bring joy and beauty to the world.", "About", "", "inherit", "open", "open", "", "80-revision-v1", "", "", "2015-01-14 16:23:56", "2015-01-15 00:23:56", "", "80", "http://agwp.dev/2015/01/80-revision-v1/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("143", "1", "2015-01-14 16:27:38", "2015-01-15 00:27:38", "Awaken Group is a multi-disciplinary Transformation Design (TD) firm that integrates strategy, leadership, and innovation to design human-centered experiences. We sit at the intersection of business, people, and design to provide purpose-driven sustainable solutions. Simply put, we Consult + Coach + Design + Digitize.\r\n\r\nWe work in the intangible (vision, strategy, leadership, organization, CEO coaching, process) and tangible (experience design, space, product/service innovation, branding) to design transformative experiences from the inside out to bring joy and beauty to the world.", "About", "", "inherit", "open", "open", "", "80-revision-v1", "", "", "2015-01-14 16:27:38", "2015-01-15 00:27:38", "", "80", "http://agwp.dev/2015/01/80-revision-v1/", "0", "revision", "", "0");
